"use strict";$(function(){var a="http://"+window.location.host+"/";$(".wk-head-search").typeahead({minLength:2,maxItem:20,order:"asc",hint:true,group:true,maxItemPerGroup:5,backdrop:false,dynamic:true,filter:false,emptyTemplate:'未找到 "{{query}}" 的相关信息',display:["sentence"],source:{"股票":{url:[a+"ajax/ajax_search.php?message={{query}},","stock"]},"行业":{url:[a+"ajax/ajax_search.php?message={{query}},","hy"]},"概念":{url:[a+"ajax/ajax_search.php?message={{query}},","gn"]},"热点事件":{url:[a+"ajax/ajax_search.php?message={{query}},","event"]}},callback:{onClickAfter:function(d,b,c){console.log(c);if(c.display!==""){switch(c.group){case"股票":window.open(a+"stocks.php?stock="+c.display.substring(c.display.indexOf("(")+1,c.display.indexOf(")")),"_blank");break;case"行业":window.open(a+"industry.php?name="+c.display,"_blank");break;case"概念":window.open(a+"concept.php?name="+c.display,"_blank");break;case"热点事件":window.open(a+"event.php?name="+c.display,"_blank");break;default:window.open(a+"error.php","_blank");break}}}}})});