"use strict";$(function(){var a="http://"+window.location.host+"/";$(".wk-head-search").typeahead({minLength:2,maxItem:20,order:"asc",hint:true,group:true,maxItemPerGroup:5,backdrop:false,dynamic:true,filter:false,emptyTemplate:'未找到 "{{query}}" 的相关信息',source:{"股票":{url:[a+"ajax/ajax_search.php?message={{query}},","stock"]},"行业":{url:[a+"ajax/ajax_search.php?message={{query}},","hy"]},"概念":{url:[a+"ajax/ajax_search.php?message={{query}},","gn"]},"热点事件":{url:[a+"ajax/ajax_search.php?message={{query}},","event"]}},callback:{onClickAfter:function(d,b,c){if(c.display!==""){switch(c.group){case"股票":window.open(a+"stocks.php?stock="+c.display.substring(c.display.indexOf("(")+1,c.display.indexOf(")")),"_blank");break;case"行业":window.open(a+"industry.php?name="+c.display,"_blank");break;case"概念":window.open(a+"concept.php?name="+c.display,"_blank");break;case"热点事件":window.open(a+"event.php?name="+c.display,"_blank");break;default:window.open(a+"error.php","_blank");break}}}}});Number.prototype.toWanNum=function(){var b=this;return(b/10000).toFixed(2)+"万"};Number.prototype.toChineseNum=function(){var b=this;if(b==0){return b}if(b>9999&&b<99999999){return(b/10000).toFixed(2)+"万"}if(b>=100000000){return(b/100000000).toFixed(2)+"亿"}return b.toFixed(2)};Number.prototype.toFormatNum=function(){var b=this;return b&&(b.toString().indexOf(".")!=-1?b.toString().replace(/(\d)(?=(\d{3})+\.)/g,function(d,c){return c+","}):b.toString().replace(/(\d)(?=(\d{3})+\b)/g,function(d,c){return c+","}))};$("i[data-toggle='popover']").popover({container:"body",trigger:"hover"});$(".wk-topshow-dp label").html(Utility.getTradeTime()).addClass("wk-up")});